from queue import Queue as q
from time import time



prompt1 = "Enter a series of digits:"
prompt2 = "Enter the desired length of adjacent digits:"
response1 = input(prompt1)
response2 = input(prompt2)
start = time()
nums, length = [int(x) for x in response1], int(response2)
product_arr = q(length)
largest = (0,)
def compute_product_of_iterable(n) -> int:
    l = tuple(n.queue)
    assert type(l[0]) == (int or float), "iterable must be int or float!"
    result = 1
    for num in l:
        result *= num
    return result
i = 1
for num in nums:
    i += 1
    if num == 0:
       product_arr = q(length)
    elif not product_arr.full():
        product_arr.put(num)
        if product_arr.full():
            t = compute_product_of_iterable(product_arr)
            if t > largest[0]:
                largest = (t, tuple(product_arr.queue), i)
    else:
        product_arr.get()
        product_arr.put(num)
        t = compute_product_of_iterable(product_arr)
        if t > largest[0]:
            largest = (t, tuple(product_arr.queue), i)

print(f"the combination {largest[1]} starting on the {largest[2]} digit \
results in {largest[0]}")
print(f"this script took {(time() - start)*1000000} nanoseconds")

